actions.script:
setupPolicyMultiplier();
initializePolicyViews(runtime);
initializeStatusViews(runtime);
createPolicyEffectViews(runtime);
setLevelVariables(0, runtime);
initializeTileBiome(runtime);
updateFSM();

updateAllStatus(runtime);
updateAllCrisis(runtime);
updateAllPolicies(runtime);
updateFSM();
updateStatusView(runtime);
updateCrisisView(runtime);
updateFiscalViews(runtime);
checkGameOverCondition(runtime);

const slider = runtime.objects.Slider.getPickedInstances()[0];
const crisis = slider.instVars['id'].replace("_crisis_slider", "");
setCrisisValue(crisis, slider.instVars['value']);
updateFSM();
updateStatusView(runtime);
updateCrisisView(runtime);

const slider = runtime.objects.Slider.getPickedInstances()[0];
const status = slider.instVars['id'].replace("_status_slider", "");
setStatusValue(status, slider.instVars['value']);
updateStatusView(runtime);
updateCrisisView(runtime);

const slider = runtime.objects.Slider.getPickedInstances()[0];
const policyName = runtime.globalVars.policyToSet;
console.log(policyName);
const policyData = policy[policyName];
const costText = getTextById("policy_pop_up_cost_slider");
const cost = policyData.minCost + (slider.instVars['value'] / 100 * (policyData.maxCost - policyData.minCost));
setSliderValue(slider, costText, slider.instVars['value'], cost.toString());

const revenueText = getTextById("policy_pop_up_revenue_slider");
const revenue = policyData.minRevenue + (slider.instVars['value'] / 100 * (policyData.maxRevenue - policyData.minRevenue));
setSliderValue(slider, revenueText, slider.instVars['value'], revenue.toString());

updatePolicyEffectViews(policyName, slider.instVars['value']);
